CREATE TABLE IF NOT EXISTS `migrations` (`id` INT AUTO_INCREMENT PRIMARY KEY, `migration` VARCHAR(255) NOT NULL, `batch` INT NOT NULL);
CREATE TABLE IF NOT EXISTS `users` (`id` INT AUTO_INCREMENT PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `email` VARCHAR(255) NOT NULL, `email_verified_at` DATETIME, `password` VARCHAR(255) NOT NULL, `remember_token` VARCHAR(100), `created_at` DATETIME,`updated_at` DATETIME);
CREATE TABLE IF NOT EXISTS `password_reset_tokens` (`email` VARCHAR(255) NOT NULL, `token` VARCHAR(255) NOT NULL, `created_at` DATETIME, PRIMARY KEY (`email`));
CREATE TABLE IF NOT EXISTS `sessions` (`id` VARCHAR(255) NOT NULL, `user_id` INT, `ip_address` VARCHAR(255), `user_agent` TEXT, `payload` TEXT NOT NULL, `last_activity` INT NOT NULL, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS `cache` (`key` VARCHAR(255) NOT NULL, `value` TEXT NOT NULL, `expiration` INT NOT NULL, PRIMARY KEY (`key`));
CREATE TABLE IF NOT EXISTS `cache_locks` (`key` VARCHAR(255) NOT NULL, `owner` VARCHAR(255) NOT NULL, `expiration` INT NOT NULL, PRIMARY KEY (`key`));
CREATE TABLE IF NOT EXISTS `jobs` (`id` INT AUTO_INCREMENT PRIMARY KEY, `queue` VARCHAR(255) NOT NULL, `payload` TEXT NOT NULL, `attempts` INT NOT NULL, `reserved_at` INT, `available_at` INT NOT NULL, `created_at` INT NOT NULL);
CREATE TABLE IF NOT EXISTS `job_batches` (`id` VARCHAR(255) NOT NULL, `name` VARCHAR(255) NOT NULL, `total_jobs` INT NOT NULL, `pending_jobs` INT NOT NULL, `failed_jobs` INT NOT NULL, `failed_job_ids` TEXT NOT NULL, `options` TEXT, `cancelled_at` INT, `created_at` INT NOT NULL, `finished_at` INT, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS `failed_jobs` (`id` INT AUTO_INCREMENT PRIMARY KEY, `uuid` VARCHAR(100) NOT NULL, `connection` TEXT NOT NULL, `queue` TEXT NOT NULL, `payload` TEXT NOT NULL, `exception` TEXT NOT NULL, `failed_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS `events` (`id` INT AUTO_INCREMENT PRIMARY KEY, `user_id` INT NOT NULL, `title` VARCHAR(100) NOT NULL, `date` DATE NOT NULL, `time` TIME, `location` VARCHAR(100), `description` TEXT, `check_in_option` VARCHAR(100) NOT NULL, `created_at` DATETIME, `updated_at` DATETIME, FOREIGN KEY(`user_id`) REFERENCES `users`(`id`) ON DELETE CASCADE);
CREATE UNIQUE INDEX `users_email_unique` ON `users` (`email`);
CREATE INDEX `sessions_user_id_index` ON `sessions` (`user_id`);
CREATE INDEX `sessions_last_activity_index` ON `sessions` (`last_activity`);
CREATE INDEX `jobs_queue_index` ON `jobs` (`queue`);
CREATE UNIQUE INDEX `failed_jobs_uuid_unique` ON `failed_jobs` (`uuid`);
